import React, { useState, useEffect } from "react";
import axios from "axios";

const ImageFetching = () => {

    const [imageTitle, setImageTitle] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5001/HomeImages")
      .then((res) => {
        setImageTitle(res.data);
      })
      .catch((error) => {
        console.error("Error fetching courses:", error);
      });
  }, []);

  return (
    <div>
      <h1>images</h1>
      {imageTitle.map((imageName) => (
            <p key={imageName.images_id }>
              {imageName.image_title}
            </p>
          ))}

<table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Home Banner Images</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {imageTitle.map((image) => (
              <tr key={image.id}>
                <td>{image.id}</td>
                <td>
                  <img
                    src={`data:image/png;base64,${image.imageData}`}
                    alt={`Image ${image.id}`}
                    style={{ width: 400 }}
                  />

                  {/* <img
                    src={image.imageData}
                    alt={`Image ${image.id}`}
                    style={{ width: 400 }}
                  /> */}
                </td>
                <td className="action">
                  <Tooltip title="Delete" arrow>
                    <Button>
                      <span className="material-symbols-outlined">delete</span>{" "}
                    </Button>
                  </Tooltip>
                  <Tooltip title="Update" arrow>
                    <Button>
                      <span className="material-symbols-outlined">update</span>{" "}
                    </Button>
                  </Tooltip>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
    </div>
  )
}

export default ImageFetching
