import React, { useState, useEffect } from "react";
import axios from "axios";
import Button from "@mui/material/Button";
import Tooltip from "@mui/material/Tooltip";

import "../styles/ImageFetching.css";

const ImageFetching = () => {
  const [imageTitle, setImageTitle] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5001/ImageTitle")
      .then((res) => {
        setImageTitle(res.data);
      })
      .catch((error) => {
        console.error("Error fetching courses:", error);
      });
  }, []);




  const [imageData, setImageData] = useState(null);

  useEffect(() => {
    axios
      .get("http://localhost:5001/HomeImages")
      .then((res) => {
        setImageData(res.data.imageData);
      })
      .catch((error) => {
        console.error("Error fetching images:", error);
      });
  }, []);



  return (
    <div>
      <h1>Images</h1>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Images</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {imageTitle.map((imageName) => (
            <tr key={imageName.images_id}>
              <td>{imageName.images_id}</td>
              <td>{imageName.image_title}</td>
              <td className="action">
                <Tooltip title="Delete" arrow>
                  <Button>
                    <span className="material-symbols-outlined">delete</span>{" "}
                  </Button>
                </Tooltip>
                <Tooltip title="Update" arrow>
                  <Button>
                    <span className="material-symbols-outlined">update</span>{" "}
                  </Button>
                </Tooltip>
              </td>
            </tr>
          ))}
        </tbody>
      </table><br />


      {/* {imageData ? (
        <img src={imageData} alt="Fetched Image" style={{ maxWidth: "100%" }} />
      ) : (
        <p>Loading image...</p>
      )} */}

{imageArray.length > 0 ? (
        <div>
          {imageArray.map((image) => (
            <img
              key={image.id}
              src={image.imageData}
              alt={`Image ${image.id}`}
              style={{ maxWidth: "100%", marginBottom: "10px" }}
            />
          ))}
        </div>
      ) : (
        <p>No images found.</p>
      )}
    </div>
  );
};

export default ImageFetching;
